import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FaChevronRight } from 'react-icons/fa';

const MenuContainer = styled.div`
  width: 100%;
  background-color: #fff;
  border-radius: 4px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  margin-bottom: 20px;
`;

const MenuItem = styled.div`
  position: relative;
  border-bottom: 1px solid #eee;

  &:last-child {
    border-bottom: none;
  }

  &:hover .submenu {
    display: block;
  }
`;

const MenuLink = styled(Link)`
  display: flex;
  align-items: center;
  padding: 12px 15px;
  color: #333;
  text-decoration: none;
  transition: all 0.2s;

  &:hover {
    background-color: #f5f9ff;
    color: #0066cc;
  }

  svg {
    margin-left: auto;
    color: #999;
    font-size: 0.8rem;
  }
`;

const MenuIcon = styled.span`
  display: flex;
  align-items: center;
  margin-right: 12px;
  color: ${props => props.color || '#0066cc'};
  font-size: 1.2rem;
`;

const MenuText = styled.span`
  flex: 1;
`;

const SubMenu = styled.div`
  display: none;
  position: absolute;
  left: 100%;
  top: 0;
  width: 250px;
  background-color: white;
  border: 1px solid #eee;
  box-shadow: 2px 2px 8px rgba(0, 0, 0, 0.1);
  z-index: 100;
  padding: 10px 0;
  border-radius: 4px;
`;

const SubMenuItem = styled(Link)`
  display: block;
  padding: 8px 15px;
  color: #555;
  text-decoration: none;

  &:hover {
    background-color: #f5f9ff;
    color: #0066cc;
  }
`;

const CategoryMenu = () => {
  // Product categories data structure with icons
  const categories = [
    {
      id: 'but-viet',
      name: 'Bút viết',
      icon: '✏️',
      color: '#3498db',
      subcategories: [
        { id: 'but-bi', name: 'Bút bi, bút mực, bút chì' },
        { id: 'but-da-quang', name: 'Bút dạ quang, bút lông dầu' },
        { id: 'but-gel', name: 'Bút gel, bút máy, bút dạ bảng' },
        { id: 'but-xoa', name: 'Bút xoá, bút thử điện' }
      ]
    },
    {
      id: 'van-phong-pham',
      name: 'Văn phòng phẩm',
      icon: '📎',
      color: '#27ae60',
      subcategories: [
        { id: 'kep-giay', name: 'Kẹp giấy, kẹp bướm, gim bấm' },
        { id: 'bang-keo', name: 'Băng keo, hồ dán, kéo' },
        { id: 'tap-vo', name: 'Tập vở, sổ ghi chép, sổ tay' },
        { id: 'bia-ho-so', name: 'Bìa hồ sơ, bìa nhựa, file đựng giấy' },
        { id: 'may-dap-ghim', name: 'Máy dập ghim, bấm lỗ, dao rọc giấy' }
      ]
    },
    {
      id: 'dung-cu-hoc-tap',
      name: 'Dụng Cụ Học Tập',
      icon: '📐',
      color: '#00bcd4',
      subcategories: [
        { id: 'thuoc-ke', name: 'Thước kẻ, ê ke, compa' },
        { id: 'bang-ve', name: 'Bảng vẽ, bảng con, phấn viết' },
        { id: 'cap-sach', name: 'Cặp sách, balo, hộp bút' },
        { id: 'tay', name: 'Tẩy, chuốt bút chì' }
      ]
    },
    {
      id: 'my-thuat',
      name: 'Mỹ Thuật',
      icon: '🎨',
      color: '#e67e22',
      subcategories: [
        { id: 'mau-nuoc', name: 'Màu nước, màu sáp, màu acrylic' },
        { id: 'but-long-ve', name: 'Bút lông vẽ, cọ vẽ, bảng pha màu' },
        { id: 'dat-nan', name: 'Đất nặn, giấy mỹ thuật, giấy màu' },
        { id: 'canvas', name: 'Canvas vẽ tranh, giá vẽ' }
      ]
    },
    {
      id: 'giay-in',
      name: 'Giấy In',
      icon: '📄',
      color: '#3498db',
      subcategories: [
        { id: 'giay-a3', name: 'Giấy A3' },
        { id: 'giay-a4', name: 'Giấy A4' },
        { id: 'giay-a5', name: 'Giấy A5' },
        { id: 'ik-copy', name: 'IK Copy' },
        { id: 'combo-5-ream', name: 'Combo 5 ream Tặng 2 bút lông dầu' },
        { id: 'combo-10-ream', name: 'Combo 10 ream Tặng 1 ream' },
        { id: 'combo-giay-in', name: 'Combo giấy in văn phòng' }
      ]
    },
    {
      id: 'but-cao-cap',
      name: 'Bút cao cấp',
      icon: '🖋️',
      color: '#e74c3c',
      subcategories: [
        { id: 'but-may-cao-cap', name: 'Bút máy cao cấp, bút ký tên' },
        { id: 'but-bi-thuong-hieu', name: 'Bút bi thương hiệu, bút quà tặng' },
        { id: 'but-khac-ten', name: 'Bút khắc tên, bút làm quà tặng doanh nghiệp' }
      ]
    },
    {
      id: 'steam-diy',
      name: 'STEAM & DIY',
      icon: '🤖',
      color: '#9b59b6',
      subcategories: [
        { id: 'bo-lap-rap', name: 'Bộ lắp ráp mô hình' },
        { id: 'do-choi-khoa-hoc', name: 'Đồ chơi khoa học, STEM' },
        { id: 'robot-lap-trinh', name: 'Robot lập trình, kit Arduino, Raspberry Pi' },
        { id: 'dung-cu-diy', name: 'Dụng cụ DIY, keo nến, máy cắt laser mini' }
      ]
    },
    {
      id: 'sach',
      name: 'Sách',
      icon: '📚',
      color: '#2980b9',
      subcategories: [
        { id: 'sach-giao-khoa', name: 'Sách giáo khoa, sách tham khảo' },
        { id: 'truyen-tranh', name: 'Truyện tranh, tiểu thuyết, sách kỹ năng' },
        { id: 'sach-ngoai-ngu', name: 'Sách học ngoại ngữ, từ điển' },
        { id: 'sach-to-mau', name: 'Sách tô màu, sách vẽ tranh' }
      ]
    },
    {
      id: 'qua-tang-lifestyle',
      name: 'Quà tặng - Lifestyle',
      icon: '🎁',
      color: '#f1c40f',
      subcategories: [
        { id: 'qua-tang-van-phong', name: 'Quà tặng văn phòng' },
        { id: 'do-trang-tri', name: 'Đồ trang trí' },
        { id: 'phu-kien-thoi-trang', name: 'Phụ kiện thời trang' },
        { id: 'do-dung-ca-nhan', name: 'Đồ dùng cá nhân' }
      ]
    }
  ];

  return (
    <MenuContainer>
      {categories.map(category => (
        <MenuItem key={category.id}>
          <MenuLink to={`/products?category=${category.id}`}>
            <MenuIcon color={category.color}>{category.icon}</MenuIcon>
            <MenuText>{category.name}</MenuText>
            <FaChevronRight />
          </MenuLink>
          <SubMenu className="submenu">
            {category.subcategories.map(subcategory => (
              <SubMenuItem 
                key={subcategory.id} 
                to={`/products?category=${category.id}&subcategory=${subcategory.id}`}
              >
                {subcategory.name}
              </SubMenuItem>
            ))}
          </SubMenu>
        </MenuItem>
      ))}
    </MenuContainer>
  );
};

export default CategoryMenu;
